import React, { useState } from 'react';
import orel from './img/orel.png'
import reshka from './img/reshka.png'

function App() {
  const [результат, setРезультат] = useState(null);

  const подброситьМонету = () => {
    const случайноеЧисло = Math.random();
    const новыйРезультат = случайноеЧисло < 0.5 ? 'orel' : 'reshka';
    setРезультат(новыйРезультат);
  };

  return (
    <div className="App">
      {результат && (
        <div className="результат">
          <img src={результат === 'orel' ? orel : reshka} alt={результат} />
          <p>{результат}</p>
        </div>
      )}
      <button onClick={подброситьМонету} className="coinflipstart">
        Подбросить монету
      </button>
    </div>
  );
}

export default App;
